import React, { Component } from 'react';
import { connect } from 'react-redux';

class Game extends Component {
  constructor() {
    super();

    this.state = {
      attempt: '',
    }
  }

  componentDidUpdate(prevProps) {
    const { keyDown } = this.props;
    if (prevProps.keyDown !== keyDown) {
      this.setState({ attempt: keyDown });
    }
  }

  handleKeyDown = ({ key }) => {
    console.log(key);
  }

  render() {
    const { attempt } = this.state;
    if (attempt === 'Enter') console.log('Finalizou');
    return (
      <main>
        <form id="attempt-1">
          <input id="a1-l1" type="text" className="wordBox" maxLength="1" value={ attempt } />
          <input id="a1-l2" type="text" className="wordBox" maxLength="1" onKeyDown={ this.handleKeyDown } />
          <input id="a1-l3" type="text" className="wordBox" maxLength="1" />
          <input id="a1-l4" type="text" className="wordBox" maxLength="1" />
          <input id="a1-l5" type="text" className="wordBox" maxLength="1" />
        </form>
      </main>
    );
  }
}

const mapStateToProps = ({ keyDown }) => ({
  keyDown,
});

export default connect(mapStateToProps)(Game);
