import React, { Component } from 'react';
import { connect } from 'react-redux';

class Game extends Component {
  constructor() {
    super();

    this.state = {
      keyTyped: '',
    }
  }

  componentDidUpdate(prevProps) {
    const { keyDown } = this.props;
    if (prevProps.keyDown !== keyDown) {
      this.setState({ keyTyped: keyDown });
    }
  }

  handleKeyDown = ({ key }) => {
    console.log(key);
  }

  handleEnterKey = () => {
    console.log('Finalizou');
  }

  render() {
    const { keyTyped } = this.state;
    if (keyTyped === 'Enter') this.handleEnterKey();
    return (
      <main>
        <form id="attempt-1">
          <input id="a1-l1" type="text" className="wordBox" maxLength="1" value={ keyTyped } />
          <input id="a1-l2" type="text" className="wordBox" maxLength="1" onKeyDown={ this.handleKeyDown } />
          <input id="a1-l3" type="text" className="wordBox" maxLength="1" />
          <input id="a1-l4" type="text" className="wordBox" maxLength="1" />
          <input id="a1-l5" type="text" className="wordBox" maxLength="1" />
        </form>
      </main>
    );
  }
}

const mapStateToProps = ({ keyDown }) => ({
  keyDown,
});

export default connect(mapStateToProps)(Game);
