import React, { Component } from 'react';
import { connect } from 'react-redux';
import { sendKeyDown } from '../store/actions';
import { ATTEMPTS } from '../services/constants';

class Game extends Component {
  constructor() {
    super();

    this.state = {
      keyTyped: '',
      words: {
        attempt1: {
          letter1: '',
          letter2: '',
          letter3: '',
          letter4: '',
          letter5: '',
        },
        attempt2: {
          letter1: '',
          letter2: '',
          letter3: '',
          letter4: '',
          letter5: '',
        },
        attempt3: {
          letter1: '',
          letter2: '',
          letter3: '',
          letter4: '',
          letter5: '',
        },
        attempt4: {
          letter1: '',
          letter2: '',
          letter3: '',
          letter4: '',
          letter5: '',
        },
        attempt5: {
          letter1: '',
          letter2: '',
          letter3: '',
          letter4: '',
          letter5: '',
        },
        attempt6: {
          letter1: '',
          letter2: '',
          letter3: '',
          letter4: '',
          letter5: '',
        },
      }
    }
  }

  componentDidMount() {
    return document.querySelector('#a1-l1').focus();
  }

  componentDidUpdate(prevProps) {
    const { keyDown } = this.props;
    if (prevProps.keyDown !== keyDown) {
      this.setState({ keyTyped: keyDown });
    }
  }

  focusNextLetter = (currentLetter) => {
    const lastNumberId = Number(currentLetter.substring(4, 5)) + 1;
    let nextLetter = document.querySelector(`#${currentLetter.substring(0, 4) + lastNumberId}`);

    if (lastNumberId > 5) {
      const nextAttempt = Number(currentLetter.substring(1, 2)) + 1;
      nextLetter = document.querySelector(`#a${nextAttempt}-l1`);
    }

    return nextLetter.focus();
  };

  handleChange = ({ nativeEvent: { data: key }, target: { id } }) => {
    const attempt = `attempt${id.slice(1 , 2)}`;
    const letter = `letter${id.slice(4, 5)}`;

    const { setKey } = this.props;

    console.log(attempt);

    this.setState((prevState) => ({
      ...prevState,
      words: {
        ...prevState.words,
        [attempt]: {
          ...prevState.words[attempt],
          [letter]: key,
    }}}));

    setKey(key);
    this.focusNextLetter(id);
  }

  handleKeyDown = ({ key }) => {
    console.log(key);
    if (key === 'Enter') this.handleEnterKey();
  }

  handleEnterKey = () => {
    console.log('Chamar função que coloca a primeira letter box da próxima tentativa em seleção');
  }

  renderForm = () => {
    const { words: {
      attempt1: { letter1: a1l1, letter2, letter3, letter4, letter5,  },
      // attempt2: { letter1, letter2, letter3, letter4, letter5,  },
      // attempt3: { letter1, letter2, letter3, letter4, letter5,  },
      // attempt4: { letter1, letter2, letter3, letter4, letter5,  },
      // attempt5: { letter1, letter2, letter3, letter4, letter5,  },
      // attempt6: { letter1, letter2, letter3, letter4, letter5,  },
    } } = this.state;
    let formRendered = [];
    for (let i = 1; i <= ATTEMPTS; i += 1) {
      formRendered.push(
        <form id={`attempt-${i}`}>
          <input id={`a${i}-l1`} type="text" className="wordBox" maxLength="1" value={ a1l1 } onChange={ this.handleChange } />
          <input id={`a${i}-l2`} type="text" className="wordBox" maxLength="1" value={ letter2 } onChange={ this.handleChange } />
          <input id={`a${i}-l3`} type="text" className="wordBox" maxLength="1" value={ letter3 } onChange={ this.handleChange } />
          <input id={`a${i}-l4`} type="text" className="wordBox" maxLength="1" value={ letter4 } onChange={ this.handleChange } />
          <input id={`a${i}-l5`} type="text" className="wordBox" maxLength="1" value={ letter5 } onChange={ this.handleChange } onKeyDown={ this.handleKeyDown } />
        </form>
      );
    }
    return formRendered;
  }

  render() {
    const { keyTyped } = this.state;

    if (keyTyped === 'Enter') this.handleEnterKey();

    // for (let i = 1; i <= 5; i += 1) {
    //   if (eval(`letter${i}`).length === 0) console.log(`Letra ${i}`);
    // }

    return (
      <main>
        { this.renderForm() }
      </main>
    );
  }
}

const mapStateToProps = ({ keyDown }) => ({
  keyDown,
});

const mapDispatchToProps = (dispatch) => ({
  setKey: (key) => dispatch(sendKeyDown(key)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Game);
