import React, { Component } from 'react';
import { WORD_LENGTH, ATTEMPTS } from '../services/constants';

class Game extends Component {
  constructor() {
    super();
  }

  render() {
    let wordLine = [];
    let phrase = [];

    for (let i = 0; i < WORD_LENGTH; i += 1) {
      wordLine.push(
        <input id={`letter-${i}`} type="text" className="wordBox" maxLength="1" />
      );
    }

    for (let i = 0; i < ATTEMPTS; i += 1) {
      phrase.push(wordLine);      
    }

    console.log(phrase);

    return (
      <main>
        {
          phrase.map((word, index) => {
            return (
              <div id={ `attempt-${index}` } key={ index }>
                {word}
              </div>
            )})
        }
      </main>
    );
  }
}

export default Game;
