import React, { Component } from 'react';
import { connect } from 'react-redux';

class Game extends Component {
  constructor() {
    super();

    this.state = {
      keyTyped: '',
      words: {
        attempt1: {
          letter1: '',
          letter2: '',
          letter3: '',
          letter4: '',
          letter5: '',
        }
      }
    }
  }

  componentDidUpdate(prevProps) {
    const { keyDown } = this.props;
    if (prevProps.keyDown !== keyDown) {
      this.setState({ keyTyped: keyDown });
      console.log(this.state.words.attempt1.letter1.length);
    }
  }

  handleKeyDown = ({ key }) => {
    console.log(key);
  }

  handleEnterKey = () => {
    console.log('Finalizou');
  }

  render() {
    const { keyTyped, words: { attempt1: { letter1, letter2, letter3, letter4, letter5,  } } } = this.state;

    if (keyTyped === 'Enter') this.handleEnterKey();

    for (let i = 1; i <= 5; i += 1) {
      if ([`letter${i}`].length === 0) console.log(`Letra ${i}`);
      else console.log(eval(`letter${i}`));
    }

    return (
      <main>
        <form id="attempt-1">
          <input id="a1-l1" type="text" className="wordBox" maxLength="1" value={ letter1 } onKeyDown={ this.handleKeyDown }/>
          <input id="a1-l2" type="text" className="wordBox" maxLength="1" value={ letter2 } onKeyDown={ this.handleKeyDown } />
          <input id="a1-l3" type="text" className="wordBox" maxLength="1" value={ letter3 } onKeyDown={ this.handleKeyDown } />
          <input id="a1-l4" type="text" className="wordBox" maxLength="1" value={ letter4 } onKeyDown={ this.handleKeyDown } />
          <input id="a1-l5" type="text" className="wordBox" maxLength="1" value={ letter5 } onKeyDown={ this.handleKeyDown } />
        </form>
      </main>
    );
  }
}

const mapStateToProps = ({ keyDown }) => ({
  keyDown,
});

export default connect(mapStateToProps)(Game);
