import React, { Component } from 'react';
import { WORD_LENGTH, ATTEMPTS } from '../services/constants';

class Game extends Component {
  constructor() {
    super();
  }

  render() {
    let wordLine = [];
    let phrase = [];

    for (let i = 0; i < WORD_LENGTH; i += 1) {
      wordLine.push(
        <input key={ i } id={ `letter-${i}` } type="text" className="wordBox" maxLength="1" />
      );
    }

    for (let i = 0; i < ATTEMPTS; i += 1) {
      phrase.push(wordLine);      
    }

    console.log(phrase);

    return (
      <main>
        <form id="attempt-1">
          <input id="a1-l1" type="text" className="wordBox" maxLength="1" />
          <input id="a1-l2" type="text" className="wordBox" maxLength="1" />
          <input id="a1-l3" type="text" className="wordBox" maxLength="1" />
          <input id="a1-l4" type="text" className="wordBox" maxLength="1" />
          <input id="a1-l5" type="text" className="wordBox" maxLength="1" />
        </form>
      </main>
    );
  }
}

export default Game;
