import React, { Component } from 'react';

class Game extends Component {
  constructor() {
    super();
  }

  render() {
    let wordLine = [];

    for (let i = 0; i < 5; i += 1) {
      wordLine.push(
        <input
          key={ i }
          type="text"
          className="wordBox"
          maxLength="1"
          pattern="[A-Za-z]"
        />
      )
    }

    return (
      <main>
        <form>
          <input id="letter1" type="text" className="wordBox" maxLength="1" />
          <input id="letter2" type="text" className="wordBox" maxLength="1" />
          <input id="letter3" type="text" className="wordBox" maxLength="1" />
          <input id="letter4" type="text" className="wordBox" maxLength="1" />
          <input id="letter5" type="text" className="wordBox" maxLength="1" />
        </form>
      </main>
    );
  }
}

export default Game;
