import React, { Component } from 'react';
import { connect } from 'react-redux';
import { sendKeyDown, lastLetter } from '../store/actions';
import { KEYS } from '../services/constants';

class Keyboard extends Component {
  constructor() {
    super();
  }

  componentDidUpdate(prevProps) {
    const { lastLetter } = this.props;
    const enterBtn = document.querySelector('#ENTER');

    console.log(lastLetter);

    if (prevProps.lastLetter !== lastLetter) {
      console.log('entrou');
      enterBtn.disabled = false;
    }
  }

  handleClick = ({ target: { id } }) => {
    const { setKey } = this.props;
    // console.log(id);
    setKey(id);
  }

  render() {
    return (
      <footer>
        {
          KEYS.map((key, index) => (
            <button
              id={ key }
              type="button"
              key={ index }
              onClick={ this.handleClick }
              className="key"
              disabled={ key === 'ENTER' ? true : false }
            >
              {key}
            </button>
          ))
        }
      </footer>
    );
  }
}

const mapStateToProps = ({ lastLetter }) => ({
  lastLetter,
});

const mapDispatchToProps = (dispatch) => ({
  setKey: (key) => dispatch(sendKeyDown(key)),
  enableEnter: () => dispatch(enableEnter()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Keyboard);
