import React, { Component } from 'react';
import { connect } from 'react-redux';

class Game extends Component {
  constructor() {
    super();

    this.state = {
      attempt: 1,
    }
  }

  componentDidMount() {
    const { currentWord } = this.props;
    this.setState({ attempt: currentWord});
  }

  render() {
    return (
      <main>
        <form id="attempt-1">
          <input id="a1-l1" type="text" className="wordBox" maxLength="1" />
          <input id="a1-l2" type="text" className="wordBox" maxLength="1" />
          <input id="a1-l3" type="text" className="wordBox" maxLength="1" />
          <input id="a1-l4" type="text" className="wordBox" maxLength="1" />
          <input id="a1-l5" type="text" className="wordBox" maxLength="1" />
        </form>
      </main>
    );
  }
}

const mapStateToProps = ({ currentWord }) => ({
  currentWord,
});

export default connect(mapStateToProps)(Game);
